(window.webpackJsonp=window.webpackJsonp||[]).push([[209],{1282:function(t,e){t.exports={attributes:{title:"SetFit",description:"The ArgillaSetFitTrainer leverages the features of SetFit to train programmatically with Argilla.",links:[{linkText:"Argilla docs",linkLink:"https://docs.argilla.io/en/latest/guides/train_a_model.html"},{linkText:"SetFit docs",linkLink:"https://github.com/huggingface/setfit"}]},html:'<p><em>code snippet</em></p>\n<pre><code class="language-python">from argilla.training import ArgillaTrainer\n\ntrainer = ArgillaTrainer(\n    name=&quot;&lt;my_dataset_name&gt;&quot;,\n    workspace=&quot;&lt;my_workspace_name&gt;&quot;,\n    framework=&quot;setfit&quot;,\n    train_size=0.8\n)\ntrainer.update_config(num_iterations=10)\ntrainer.train(output_dir=&quot;text-classification&quot;)\nrecords = trainer.predict(&quot;The ArgillaTrainer is great!&quot;, as_argilla_records=True)\n</code></pre>\n<p><em>update training config</em></p>\n<pre><code class="language-python">trainer.update_config(\n    dev_corpus = &quot;corpora.dev&quot;,\n    train_corpus = &quot;corpora.train&quot;,\n    seed = 42,\n    gpu_allocator = 0,\n    accumulate_gradient = 1,\n    patience = 1600,\n    max_epochs = 0,\n    max_steps = 20000,\n    eval_frequency = 200,\n    frozen_components = [],\n    annotating_components = [],\n    before_to_disk = None,\n    before_update = None\n)\n</code></pre>\n'}}}]);